require(['jquery'], function($) {
  'use strict';

  function toggleForm(modalite) {
    if (modalite == 'autrement') {
      $('.demat-eval-container').hide();
      $('.raison').show();
      $('.demat-vote-container').show();
    } else {
      $('.demat-eval-container').show();
      $('.raison').hide();
      $('.demat-vote-container').hide();
    }
  }

  $(document).ready(function() {
    $('.avis-btn').click( function() {
      $('.avis-form').show();
    });

    $("input[name='Avis.Code.AvisClass_0_modalite']").change(function() {
      if(this.checked) {
        toggleForm(this.value);
      }
    });    

    var modalite = $("input[name='Avis.Code.AvisClass_0_modalite']:checked").val(); 
    toggleForm(modalite);
  });

});

/*
 * Add a little padding on desktop, so that it looks nicer
