  <dt>Query limit:</dt>
  <dd><input type="text" name="limit" value="$limit"/>
 </dl>

<button type="submit" class="btn btn-default" name="action" value="updateChamp" title="Property 'champ': update all demarches to 'national'">
  Update property "Démarche nationale / territoriale"</button>
<button type="submit" class="btn btn-default" name="action" value="updateMinisteres" title="Property 'ministeres': merge values 'MCT' and 'MTES'">
  Reaffect MCT/MTES to MECT</button>
