{{groovy}}

import org.xwiki.query.Query

class Service {

  def out, xwiki, context, services, actOnDatabase
  def logger
  def DEMARCHE_CLASS_NAME = "Demarches.Code.DemarchesClass"
  def DEMARCHE_TEMPLATE_NAME = "Demarches.Code.DemarchesTemplate"
  def DEMARCHE_CLASS_REFERENCE = services.model.resolveDocument(DEMARCHE_CLASS_NAME, 'current')
  def BASE_HQL = "select doc.fullName from XWikiDocument as doc, BaseObject as obj FROM_CLAUSE where obj.name = doc.fullName and obj.className = :demarcheClassName and doc.fullName <> :demarcheTemplateName WHERE_CLAUSE"
  def limit = 0

  Service(out, services, xwiki, context, limit, actOnDatabase) {
    this.out = out
    this.services = services
    this.xwiki = xwiki
    this.context = context
    this.limit = limit
    this.actOnDatabase = actOnDatabase
    this.logger = services.logging.getLogger("monavis.M9-MigrationScripts")
  }

  void println(message) {
    out.println(message)
  }

  Query computeQuery(fromClause, whereClause) {
    def hql = BASE_HQL.replace("FROM_CLAUSE", fromClause)
    hql = hql.replace("WHERE_CLAUSE", whereClause)
    def query = services.query.hql(hql)
    query.setLimit(limit)
    query.bindValue('demarcheClassName', DEMARCHE_CLASS_NAME)
    query.bindValue('demarcheTemplateName', DEMARCHE_TEMPLATE_NAME)
    return query
  }

  void printHeader(title, query) {
    println title
    println "=== Query ==="
    println "* Statement: {{{$query.statement}}}"
    for (parameter in query.getNamedParameters()) {
      println "* Parameter '$parameter.key': $parameter.value"
    }
    println "=== Results ==="
    if (actOnDatabase) {
      println "List of demarches that were updated:"
    } else {
      println "List of demarches that will get updated:"
    }
  }

  // Property "champ": update all values to "national"
  void updateChamp() {
    def query = computeQuery("", "")
    printHeader("== Property 'champ' value update to 'national' ==", query)
    def entries = query.execute()
    for (entry in entries) {
      def doc = xwiki.getDocument(entry, context).clone()
      def demarche = doc.getXObject(DEMARCHE_CLASS_REFERENCE)
      def value = demarche.getStringValue("champ")
      // Don't update the property value if it's already set to "national"
      if ("national" != value) {
        println "|[[$doc.title>>$doc.fullName]]"
        if (actOnDatabase) {
          logger.warn("updateChamp for {}", doc)
          demarche.set("champ", "national", context)
          xwiki.saveDocument(doc, "Mise à jour de la propriété 'Démarche nationale / territoriale' à 'national'", context)
        }
      }
    }
  }

  // Ministeres: merge MCT and MTES
  void updateMinisteres() {
    def query = computeQuery(", DBStringListProperty as prop join prop.list list", "and prop.id.id = obj.id and prop.id.name = :ministeresPropertyName and (list = 'Ministeres.MCT' or list = 'Ministeres.MTES')")
    printHeader("== Property 'ministeres': merging MCT and MTES into MECT ==", query)
    query.bindValue('ministeresPropertyName', "ministeres")
    def entries = query.execute()
    for (entry in entries) {
      def doc = xwiki.getDocument(entry, context).clone()
      def demarche = doc.getXObject(DEMARCHE_CLASS_REFERENCE)
      def ministeres = demarche.getListValue("ministeres")
      println "|[[$doc.title>>$doc.fullName]]"
      if (actOnDatabase) {
        logger.warn("Update ministeres for {}", doc)
        ministeres.remove("Ministeres.MCT")
        ministeres.remove("Ministeres.MTES")
        ministeres.add("Ministeres.MECT")
        demarche.set("ministeres", ministeres, context)
        xwiki.saveDocument(doc, "Réaffectation des ministères MCT et MTES au ministère MECT", context)
      }
    }
  }

  void propagateDbListPropertyToTrue(whereClause, dbListPropertyName, booleanPropertyName, isRealBoolean) {
    def query = computeQuery(", DBStringListProperty as prop join prop.list list", whereClause)
    query.bindValue("propertyName", dbListPropertyName)
    printHeader("== Property '$booleanPropertyName' value update ==", query)
    def entries = query.execute()
    for (entry in entries) {
      def doc = xwiki.getDocument(entry, context).clone()
      def demarche = doc.getXObject(DEMARCHE_CLASS_REFERENCE)
      println "|[[$doc.title>>$doc.fullName]]"
      if (actOnDatabase) {
        logger.warn("Update property $booleanPropertyName for {}", doc)
        if (isRealBoolean) {
          demarche.set(booleanPropertyName, 1, context)
        } else {
          demarche.set(booleanPropertyName, "oui", context)
        }
        xwiki.saveDocument(doc, "Mise à jour de la propriété '$booleanPropertyName' à 'oui'", context)
      }
    }
  }

  // En ligne entièrement : niveauDemat = formulaire or teleprocedure -> true
  // (check that "teleservice" in the specification document means "teleprocedure")
  void updateEnLigneEntierement() {
    propagateDbListPropertyToTrue("and prop.id.id = obj.id and prop.id.name = :propertyName and (list = 'formulaire' or list = 'teleprocedure')", "niveauDemat", "enLigneEntierement", true)
  }

  // caracteristiquesDemat:
  // - if franceconnect -> propagate to "franceConnect"
  // - if mobile -> propagate to "adapteMobile"
  // - apientreprises -> propagate to "api" (but not apiparticuliers, see specification)
  // - demarchesimplifiee -> propagate to demarcheSimplifiee
  void updateFranceConnect() {
    propagateDbListPropertyToTrue("and prop.id.id = obj.id and prop.id.name = :propertyName and list = 'franceconnect'", "caracteristiquesDemat", "franceConnect", false)
  }

  void updateAdapteMobile() {
    propagateDbListPropertyToTrue("and prop.id.id = obj.id and prop.id.name = :propertyName and list = 'mobile'", "caracteristiquesDemat", "adapteMobile", false)
  }

  void updateApi() {
    def query = computeQuery(", DBStringListProperty as prop join prop.list list", "and prop.id.id = obj.id and prop.id.name = :propertyName and list = 'apientreprises'")
    query.bindValue("propertyName", "caracteristiquesDemat")
    printHeader("== Property 'api' value update ==", query)
    def entries = query.execute()
    for (entry in entries) {
      def doc = xwiki.getDocument(entry, context).clone()
      def demarche = doc.getXObject(DEMARCHE_CLASS_REFERENCE)
      println "|[[$doc.title>>$doc.fullName]]"
      if (actOnDatabase) {
        logger.warn("Update api for {}", doc)
        demarche.set("api", "apientreprises", context)
        xwiki.saveDocument(doc, "Mise à jour de la propriété 'API' à 'API entreprises'", context)
      }
    }
  }

  void updateDemarcheSimplifiee() {
    propagateDbListPropertyToTrue("and prop.id.id = obj.id and prop.id.name = :propertyName and list = 'demarchesimplifiee'", "caracteristiquesDemat", "demarcheSimplifiee", true)
  }
}

def action = request.action
if (action != null) {
  def service =  new Service(out, services, xwiki.XWiki, xcontext.context, new Integer(request.limit), new Boolean(request.actOnDatabase))
  service."$action"()
}
{{/groovy}}


