/* Custom DSFR Styles                              */
/***************************************************/

/*
 * Adapt bootstrap breakpoint to dsfr breakpoint
 * dsfr md breakpoint correspond to bootstrap sm breakpoint
 * overrids bootstrap/variables.less
 */
// Point at which the navbar becomes uncollapsed
@grid-float-breakpoint: @screen-md-min;
// Point at which the navbar begins collapsing
@grid-float-breakpoint-max: (@grid-float-breakpoint - 1);

/* Remove bootstrap border-radius */
@border-radius-base: 0px;
@border-radius-small: 0px;
@border-radius-large: 0px;

/* Remove bootstrap gradients for buttons */
.btn-gradient (@color) {
  background-image: none;
  filter: none;
  background-color: @color;
}
.btn-border (@color) {
  border: 1px solid @color;
  border-bottom: 1px solid @color;
  box-shadow: none;
}

/* Add border to default button */
@btn-default-bg-hover: #efefef;
.btn-default {
  .btn-border(@btn-default-border);

  &:hover,
  &:focus {
    .btn-gradient(@btn-default-bg-hover);
    .btn-border(@btn-default-border);
  }
}

