/*
 * Navbar corrections for https://jira.xwiki.org/browse/XWIKI-17262
 * As long as the navbar is collapsed, display the header and menus completely different.
 * We're doing it like this and not mobile-first because we're already inheriting some desktop behaviour and I don't want to spend time resetting all that
 */
@media (min-width: @grid-float-breakpoint) {
  /* On desktop all the contents of the .navbar-collapse are floated so we'll cancel its standard padding from mobile where contents are not floated, to make it not take any room*/
  #menuview .navbar-collapse {
    padding-right: 0;
    padding-left: 0;
  }
}
@media (max-width: @grid-float-breakpoint-max) {
  #menuview {
    .navbar-header, .navbar-left, .navbar-right {
      float: none;
    }
    .navbar-left, .navbar-right {
      margin-top: 0;
      margin-bottom: 0;
      padding-top: 0;
      padding-bottom: 0;
    }
  }
  /* Correction of the toggle display along with some customization */
  .navbar-toggle {
    /* Custom size of the toggle for the observatoire */
    height: @logo-height-mobile;
    /* Try to align the burger with the text from the logo */
    padding-top: 0;
    padding-bottom: 12px;
    /* Reset some ugly styles */
    margin-top: 0;
    margin-bottom: 0;
    border: 0;
    border-radius: 0;
    &:focus .fa-bars {
      .outline;
      outline-offset: 2px;
    }
    /* The toggle changes text and display following whether it's open or not: ... */
    /* ... Open */
    color: @navbar-default-link-color;
    .navbar-toggle-name {
      &.navbar-toggle-name-closed {
        display: none !important;
      }
      &.navbar-toggle-name-open {
        display: initial !important;
        margin-right: 0.5em;
      }
    }
    .fa::before {
      content: "\f00d";
      font-size: 1rem;
    }
    /* ... Closed */
    &.collapsed {
      color: inherit;
      .navbar-toggle-name {
        &.navbar-toggle-name-closed {
          display: initial !important;
        }
        &.navbar-toggle-name-open {
          display: none !important;
        }
      }
      .fa::before {
        content: "\f0c9";
      }
    }
  }
  /* Reset some ugly styles */
  .navbar-default .navbar-toggle:hover, .navbar-default .navbar-toggle:focus {
    background-color: transparent;
  }
  .companylogo {
    float: left;
  }
  /* Custom elements of the observatoire */
  #menuview .wikidemarches-topmenu {
    clear: right;
    & > a {
      display: block;
      text-align: right;
    }
  }
}

