import com.opencsv.CSVWriter
import java.io.Writer

class OpenCsvWriter {

  def CSVWriter csvWriter

  OpenCsvWriter() {
  }

  String debug()  {
    return "Writer: $csvWriter"
  }

  void initialize(Writer writer) {
    this.csvWriter = new CSVWriter(writer, CSVWriter.DEFAULT_SEPARATOR, CSVWriter.DEFAULT_QUOTE_CHARACTER, CSVWriter.DEFAULT_ESCAPE_CHARACTER, CSVWriter.DEFAULT_LINE_END)
  }

  void writeNext(List list) {
    def array = list.toArray(new String[0])
    csvWriter.writeNext(array, true)
  }
}
