property.code:
require.config({
    paths: {
       'css': '$services.webjars.url('require-css/0.1.7/css.js')',
       'domReady': '$services.webjars.url('requirejs-domready/2.0.1/domReady.js')',
       'datatables': '$services.webjars.url('datatables/1.10.19/js/jquery.dataTables.js')',
       'datatables-css': '$services.webjars.url('datatables/1.10.19/css/jquery.dataTables.min')'
    }
});


require(['jquery', 'datatables', 'css!datatables-css', 'domReady!'], function ($) {

    function computeBadgeStyle(value) {
      value = value.toLowerCase();
      switch (value) {
        case 'oui':
          return "success";
        case 'non':
          return "danger";
        case 'partiel':
          return "warning";
        case 'expérimentation':
          return 'info';
        case 'déploiement partiel':
          return 'warning';
        default:
          return "default";
      }
    }

    function computeUrlScoreBadgeStyle(value) {
      if (isNaN(value))
        return "default";
      if (value >= 7)
        return "success";
      if (value >= 4 && value < 7)
        return "warning";
      return "danger";
    }

    function addBadge(value, badgeStyleFunction) {
      var badgeStyle = badgeStyleFunction.apply(this, [value]);
      return '<span class="row-details-value"><span class="label label-' + badgeStyle + '">' + value + '</span></span>';
    }

    function format (data) {
      var output = '<div class="row">' +
        '<div class="col-md-12">' +
        '<div class="row-details">' +
        '<p class="row-details-title">Qualité de la démarche</p>' +
        '<p class="row-details-subtitle">Le service en ligne&hellip;</p>' +
        '<ul>' +
        // '<li><span class="row-details-key">Administration concernée : </span><span class="row-details-value">' + data.direction + '</span></li>' +
        //'<li><span class="row-details-key">&hellip; intègre-t-il le questionnaire de satisfaction MonAvis ? </span>' + addBadgeSpan(data.statutIntegration) + '</li>' +
        '<li><span class="row-details-key">&hellip; permet-il de s\'identifier avec FranceConnect ? </span>' + addBadge(data.franceConnect, computeBadgeStyle) + '</li>' +
        '<li><span class="row-details-key">&hellip; est-il accessible sur smartphone ? </span>' + addBadge(data.adapteMobile, computeBadgeStyle) + '</li>' +
        '<li><span class="row-details-key">&hellip; propose-t-il un support de qualité ? </span>' + addBadge(data.qualiteSupport, computeBadgeStyle) + '</li>' +
        '<li><span class="row-details-key">&hellip; a-t-il un haut niveau de performance et de disponibilité ? </span>' + addBadge(data.urlScore, computeUrlScoreBadgeStyle) + '</li>';
        if (data.avisCount >= 100) {
          output = output + '<li><span class="row-details-key">Indice de satisfaction :</span><span class="row-details-value">' + data.statisfactionIndex + ' % - calculé sur un total de ' + data.avisCount + ' avis recueillis</span></li>';
        } else {
           output = output + '<li><span class="row-details-key">Bouton de vote MonAvis à intégrer</span></li>';
        }
        output = output + '</ul></div></div></div>';
      return output;
    }

    // TODO: the language labels could be loaded directly from the datatables-plugin
    var table = $('#top-demarches').DataTable({
      ajax: "$xwiki.getAttachmentURL('Demarches.Code.Top', 'data.json')",
      paging: false,
      columns: [
            {
              className:      'details-control',
              orderable:      false,
              data:           null,
              defaultContent: ''
            },
            {
              data: "title",
              className: 'column-title'
            },
            { data: "ministere" },
            { data: "groupes" },
            { data: "statutDemat",
              render: function ( data, type, row ) {
                return addBadge(data, computeBadgeStyle);
              }
            },
            {
              data: "dateMiseEnLigne",
              type: "num",
              render: {
                _: 'display',
                sort: 'value'
              }
            },
            {
              data: "volumetrie",
              type: "num",
              render: {
                _: 'display',
                sort: 'value'
              },
              className: 'column-volumetrie'
            },
            {
              data: "pourcentageRecoursVoieDematerialisee",
              type: "num",
              render: {
                _: 'display',
                sort: 'value'
              },
              className: 'column-pourcentageRecoursVoieDematerialisee'
            }
      ],
      "order": [[2, 'asc']],
      language: {
          info: "Total : _TOTAL_ démarches",
          infoEmpty: "Aucune démarche trouvée",
          infoFiltered: "(filtrage appliqué sur _MAX_ total entrées)",
          loadingRecords: "Chargement...",
          processing: "Traitement...",
          search: "Recherche :",
          zeroRecords: "Aucune entrée trouvée"
        }
      }
    );

    $('#top-demarches tbody').on('click', 'td.details-control', function () {
      var tr = $(this).closest('tr');
      var row = table.row( tr );
      if ( row.child.isShown() ) {
        row.child.hide();
        tr.removeClass('shown');
      }
      else {
        row.child( format(row.data()) ).show();
        tr.addClass('shown');
      }
    });
});

