property.code:
require.config({
    paths: {
       'css': '$services.webjars.url('require-css/0.1.7/css.js')',
       'domReady': '$services.webjars.url('requirejs-domready/2.0.1/domReady.js')',
       'datatables': '$services.webjars.url('datatables/1.10.19/js/jquery.dataTables.js')',
       'datatables-css': '$services.webjars.url('datatables/1.10.19/css/jquery.dataTables.min')'
    }
});


require(['jquery', 'datatables', 'css!datatables-css', 'domReady!'], function ($) {

    function computeBadgeKey(value) {
      value = value.toLowerCase();
      switch (value) {
        case 'oui':
          return "success";
        case 'non':
          return "danger";
        case 'partiel':
          return "warning";
        default:
          return "default";
      }
    }

    function addBadgeSpan(value) {
      var badgeKey = computeBadgeKey(value);
      return '<span class="row-details-value"><span class="label label-' + badgeKey + '">' + value + '</span></span>';
    }

    function format ( data ) {
        var output = '<div class="row">' +
              '<div class="col-md-12"><ul>' +
              '<li><span class="row-details-key">Réalisable en ligne : </span>' + addBadgeSpan(data.statutDemat) + '</li>' +
              '<li><span class="row-details-key">Date d\'ouverture du service dématérialisé : </span>' + data.dateMiseEnLigne + '</li>' +
              '<li><span class="row-details-key">Pourcentage du recours à la voie dématérialisée : </span>' + data.pourcentageRecoursVoieDematerialisee + '</li>' +
              '<li><span class="row-details-key">Administration concernée : </span>' + data.direction + '</li>' +
              '<li><span class="row-details-key">Avis MonAvis intégré ? </span>' + addBadgeSpan(data.statutIntegration) + '</li>' +
              '<li><span class="row-details-key">FranceConnect particuliers ? </span>' + addBadgeSpan(data.franceConnect) + '</li>' +
              '<li><span class="row-details-key">Accessible sur smartphone ? </span>' + addBadgeSpan(data.adapteMobile) + '</li>' +
              '<li><span class="row-details-key">Support de qualité ? </span>' + addBadgeSpan(data.qualiteSupport) + '</li>';
              if (data.avisCount >= 100) {
                output = output + '<li><span class="row-details-key">Indice de satisfaction :</span><span class="row-details-value">' + data.statisfactionIndex + ' %, calculé sur un total de ' + data.avisCount + ' avis recueillis</span></li>';
              }
              output = output +
                '</ul></div>'
            '</div>';
      return output;
    }

    // TODO: the language labels could be loaded directly from the datatables-plugin
    var table = $('#top-demarches').DataTable({
      "ajax": "$xwiki.getAttachmentURL('Demarches.Code.Top', 'data.json')",
      //"ajax": "$xwiki.getURL('Demarches.Code.Top', 'view', 'execute=1')",
      "paging": false,
      "pageLength": 100,
      "columns": [
            {
                "className":      'details-control',
                "orderable":      false,
                "data":           null,
                "defaultContent": ''
            },
            { "data": "title" },
            { "data": "ministere" },
            { "data": "groupes" },
            {
              "data": "volumetrie",
              type: "num",
              render: {
                _: 'display',
                sort: 'value'
              }
            }
      ],
      "order": [[1, 'asc']],
      language: {
          "info":           "Total : _TOTAL_ démarches",
          "infoEmpty":      "Aucune démarche trouvée",
          "infoFiltered":   "(filtrage appliqué sur _MAX_ total entrées)",
          "loadingRecords": "Chargement...",
          "processing":     "Traitement...",
          "search":         "Recherche :",
          "zeroRecords":    "Aucune entrée trouvée"
        }
      }
    );

    $('#top-demarches tbody').on('click', 'td.details-control', function () {
        var tr = $(this).closest('tr');
        var row = table.row( tr );
        if ( row.child.isShown() ) {
            row.child.hide();
            tr.removeClass('shown');
        }
        else {
            row.child( format(row.data()) ).show();
            tr.addClass('shown');
        }
    } );
});

td.details-control {
  text-align: center;
  cursor: pointer;
  vertical-align: middle;
}

td.details-control::before {
  content: "+";
  background-color: #337ab7;
  color: #fff;
  font-weight: bold;
  display: inline-block;
  line-height: 1.2em;
  width: 1.2em;
  vertical-align: top;
  border-radius: 0.1em;
}
tr.shown td.details-control::before {
  content: "-";
}


#top-demarches {
  tr.shown {
    background-color: #f6f6f6;
  }

  td.details-control {
    width: 4%;
  }

  /* Styles for the détails panel */
