      padding-top: 0px;
    }

    ul {
      list-style-type: none;
      margin-left: 0;
      padding-left: 0;
      display: table;
      width: 96%;
      margin-left: 4%;
      border-top: 0.5px dotted #555;
      padding-top: 8px;
      li {
        display: table-row;
        .row-details-key, .row-details-value {
          display: table-cell;
          padding-top: 0.2em;
          padding-bottom: 0.2em;
        }
        .row-details-key {
          width: 46.4%;
        }
      }
    }
  }

  tr.odd td:last-child, tr.even td:last-child {
    text-align: right;
  }
}

