property.code:
require.config({
  paths: {
    chart: "$services.webjars.url('org.webjars.bower:chartjs', 'dist/Chart.bundle.min.js')",
    palette: "$services.webjars.url('org.xwiki.contrib:application-chartjs-webjar', 'palette.min.js')"
  },
  shim: {
    palette: {
      exports: 'palette'
    }
  }
});

require(['jquery', 'chart', 'palette'], function($, Chart, palette) {

  var getData = function(canvas) {
    var deferred = $.Deferred();
    var data;
    try {
      data = JSON.parse(canvas.attr('data-source') || '{}');
    } catch (exception) {
      deferred.reject(exception);
    }
    if (data) {
      if (data.table && $('table#' + data.table).length > 0) {
        data = readDataFromTable(data);
      }
      deferred.resolve(data);
    }
    return deferred.promise();
  };

  var chartTypesWithBackgroundColorPerDataSet = ['line', 'bar', 'horizontalBar', 'radar'];
  var configureData = function(data, type) {
    var opacity = (type === 'line' || type === 'radar') ? .5 : .3;
    var dataSetBackgroundColor = palette('tol', data.datasets.length).map(function(hex) {
      return convertHex(hex, opacity);
    });
    data.datasets.forEach(function(dataSet, index) {
      if ((!dataSet.backgroundColor || dataSet.backgroundColor.length == 0)) {
        if (chartTypesWithBackgroundColorPerDataSet.indexOf(type) >= 0) {
          // Background color per data set.
          dataSet.backgroundColor = dataSetBackgroundColor[index];
        } else {
          // Background color per value.
          dataSet.backgroundColor = palette('tol', dataSet.data.length).map(function(hex) {
            return '#' + hex;
          });
        }
      } else {
        dataSet.backgroundColor = dataSet.backgroundColor.map(function(hex) {
          return convertHex(hex.substring(1), 0.8);
        });
      }
    });
    return data;
  };

  var convertHex = function(hex, opacity) {
    r = parseInt(hex.substring(0,2), 16);
    g = parseInt(hex.substring(2,4), 16);
    b = parseInt(hex.substring(4,6), 16);
    return 'rgba(' + r + ',' + g + ',' + b + ',' + opacity + ')';
  };

  var readDataFromTable = function(options) {
    var labels = [];
    var dataSets = [];
    var rows = $('table#' + options.table + ' tr');
    if (options.multipleDataSets) {
      if (options.dataSetLabel) {
        var dataSetsByLabel = {};
        rows.each(function() {
          var cells = $(this).children('td');
          if (cells.length < 3) {
            return;
          }
          var label = cells.first().text().trim();
          if (labels.indexOf(label) < 0) {
            labels.push(label);
          }
          var dataSetLabel = $(cells[options.dataSetLabel]).text().trim();
          var dataSet = dataSetsByLabel[dataSetLabel];
          if (!dataSet) {
            dataSet = {
              label: dataSetLabel,
              data: [],
              backgroundColor: []
            }
            dataSetsByLabel[dataSetLabel] = dataSet;
          }
          var values = [];
          for (var i = 1; i < cells.length; i++) {
            var value = $(cells[i]).text().trim();
            if ($.isNumeric(value) && i != options.dataSetLabel) {
              values.push(value);
            }
          }
          if (values.length == 1) {
            dataSet.data.push(values[0]);
          } else {
            dataSet.data.push(values);
          }
        });
        for (var dataSetLabel in dataSetsByLabel) {
          if (dataSetsByLabel.hasOwnProperty(dataSetLabel)) {
            dataSets.push(dataSetsByLabel[dataSetLabel]);
          }
        }
      } else {
        rows.each(function() {
          var cells = $(this).children('td');
          if (cells.length < 3) {
            return;
          }
          var label = cells.first().text().trim();
          labels.push(label);
          for (var i = 1, dataSetIndex = 0; i < cells.length; i++) {
            var value = $(cells[i]).text().trim();
            if ($.isNumeric(value)) {
              var dataSet = dataSets[dataSetIndex];
              if (!dataSet) {
                dataSet = {
                  label: $(rows.first().find('th')[i]).text().trim(),
                  data: [],
                  backgroundColor: []
                };
                dataSets.push(dataSet);
              }
              dataSet.data.push(value);
              dataSetIndex++;
            }
          }
        });
      }
    } else {
      var dataSet = {
        label: $(rows.first().find('th')[1]).text().trim(),
        data: [],
        backgroundColor: []
      };
      rows.each(function() {
        var cells = $(this).children('td');
        if (cells.length < 2) {
          return;
        }
        var label = cells.first().text().trim();
        var values = [];
        var backgroundColors = [];
        for (var i = 1; i < cells.length; i++) {
          var value = $(cells[i]).text().trim();
          if ($.isNumeric(value)) {
            values.push(value);
          } else {
            backgroundColors.push(value);
          }
        }
        labels.push(label);
        if (values.length == 1) {
          dataSet.data.push(values[0]);
          if (backgroundColors.length == 1) {
            dataSet.backgroundColor.push(backgroundColors[0]);
          }
        } else {
          dataSet.data.push(values);
          dataSet.backgroundColor.push(backgroundColors);
        }
      });
      dataSets.push(dataSet);
    }
    return {
      labels: labels,
      datasets: dataSets
    };
  };

  $('canvas.chart').each(function() {
    var canvas = $(this);

    // Converts a JSON attribute value to a JavaScript object.
    function jsonAttributeToObject(attributeName) {
      var object = {};
      try {
        var json = canvas.attr(attributeName);
        object = JSON.parse(json || '{}');
      } catch (exception) {
        console.log(exception)
      }
      return object;
    }

    var type = canvas.attr('data-type');
    var dataSource = canvas.attr('data-source');
    var options = jsonAttributeToObject('data-options');
    var source = jsonAttributeToObject('data-source');

    getData(canvas).then(function(data) {
      return configureData(data, type);
    }).done(function(data) {
      for (var i = 0; i < data.labels.length; i++) {
        var label = data.labels[i];
        if (label == 'Très bien' || label == 'Bon') {
          data.labels[i] = '\uf118';
        } else if (label == 'Moyen') {
          data.labels[i] = '\uf11a';
        } else if (label == 'Mauvais' || label == 'Pas bien') {
          data.labels[i] = '\uf119';
        }
      }

      if (type != 'pie') {
        options.tooltips = {
          callbacks: {
            title: function(tooltipItem, data) { return ''},
            label: function(tooltipItem, data) {
              var label = data.datasets[tooltipItem.datasetIndex].label || '';
              if (tooltipItem.xLabel) {
                return tooltipItem.xLabel + '%';
              } else {
                return label;
              }
            }
          }
        }
      } else {
        options.tooltips = {
          callbacks: {
            label: function(tooltipItem, data) {
              var index = tooltipItem.index;
              return data.labels[index] + ': ' + data.datasets[0].data[index] + '%';
            }
          }
        }
      }

      var chart = new Chart(canvas, {
        type: type,
        data: data,
        options: options
      });


      // Fallback to table data for accessibility, by moving the existing table to the canvas content
      var table = $('table#' + source.table);
      table.removeClass('hidden');
      table.appendTo(canvas);

    });
  });
});
